INSERT INTO `zy_article` VALUES ('0aee5066c079e18ef9e658492d1cc3d2', '9e3a470dc5930039fd08b1abbbaaf3e0', 1, '4', '随笔', 'Express 连接  MySql 公共连接池封装', ' 公共连接池封装 ', 0, 161, 'http://localhost:5220/zy-server/public/images?id=1644655142222f250f577a67bf.jpg', '<pre class=\"language-java\"><code>let $webSqlMap = require(\'../../sqlMap/web\') // sql语句\nlet comMethods = require(\'../../utils/common\') // 引入公共连接池\nlet tools = require(\'../../utils/tools\') // 引入工具模块\n\n/**\n *@author ZY\n *@date 2022/2/12 15:11\n *@Description:web端博文\n */\n//查询文章列表\nexports.webArticleList = async (req, res, next) =&gt; {\n    try {\n        let params = req.body, sql = \'\', total = 0;\n        let queryTotal = $webSqlMap.articleOpt.count + ` WHERE isPublish=1`\n        //多条件查询\n        if (params.params.title) {\n            sql = $webSqlMap.articleOpt.list + ` WHERE isPublish=\'${1}\' AND title=\'${params.params.title}\' ORDER BY ${params.orderBy} ${params.orderType} LIMIT ${params.size} OFFSET ${params.size * (params.current - 1)}`\n        }else {\n            sql = $webSqlMap.articleOpt.list + ` WHERE isPublish=\'${1}\' ORDER BY ${params.orderBy} ${params.orderType} LIMIT ${params.size} OFFSET ${params.size * (params.current - 1)}`\n        }\n        comMethods.queryCount(queryTotal).then(data =&gt; {\n            total = data\n        })\n        comMethods.commonQuery(sql, params).then(data =&gt; {\n            let resData = data || {}\n            resData.total = total\n            res.json(resData)\n        }).catch(err =&gt; {\n            console.log(\'--查询文章列表错误--\', err)\n        })\n\n    } catch (err) {\n        next(err)\n    }\n}\n//查询文章详情\nexports.webArticleDetail = async (req, res, next) =&gt; {\n    try {\n        let params = req.body, sql = $webSqlMap.articleOpt.list + ` WHERE id=\'${params.id}\'`\n\n        comMethods.commonQuery(sql).then(data =&gt; {\n            let resData = data || {}\n            res.json(resData)\n        }).catch(err =&gt; {\n            console.log(\'--查询文章详情错误--\', err)\n        })\n\n    } catch (err) {\n        next(err)\n    }\n}\n//修改文章\nexports.webArticleUpdate = async (req, res, next) =&gt; {\n    try {\n        let params = req.body,\n            sql = $webSqlMap.articleOpt.update,\n            queryClassData = $webSqlMap.articleClassOpt.list + ` WHERE id=\'${params.classId}\'`\n\n        //查询分类数据\n        comMethods.commonQuery(queryClassData).then(data =&gt; {\n            let resData = data || {}\n            if (data.error) {\n                res.json(resData)\n            } else {\n                let updateParams = [\n                    resData.records[0].id,\n                    resData.records[0].className,\n                    resData.records[0].classValue,\n                    params.isPublish,\n                    params.title,\n                    params.summary, 0, 0, params.img,\n                    params.content, params.isTop, params.isHot, params.pubTime,\n                    params.insertTime,\n                    tools.getDate(),\n                    params.id,\n                ]\n                comMethods.commonQuery(sql, updateParams).then(data =&gt; {\n                    let realRes = data || {}\n                    res.json(realRes)\n                })\n            }\n        })\n\n    } catch (err) {\n        next(err)\n    }\n}\n\n</code></pre>', 1, 1, '2022-02-12 13:56:16', '2022-02-12 13:46:04', '2022-02-12 22:11:42', NULL);
INSERT INTO `zy_article` VALUES ('2df8c9315be2732c1e21829df096412b', '5c041d73cd4b0d8a5e63fc71d65c84b8', 1, '3', 'JavaScript', '孩子平时很懂事，很少向我索要礼物，最近孩子想要买一个玩具但价格实在太贵，一个月工资，怎么处理更好？', '孩子平时很懂事，很少向我索要礼物，最近孩子想要买一个玩具但价格实在太贵，一个月工资，怎么处理更好？', 0, 46, 'http://localhost:5220/zy-server/public/images?id=1644655206538658b02c536fb2.jpg', '<div><br />\n<div>\n<blockquote>\n<p data-pid=\"SIMi-LGe\"><strong>在香港，如果条件允许，餐饮、酒吧、茶馆、咖啡厅等尽可能设室外座位，以减少室内集聚。在室外保持隔位距离，缩短停留时间，被感染的机会将大幅降低。</strong></p>\n</blockquote>\n<p data-pid=\"d2E3-N0w\">在香港，<strong>室内外温差相差不大时，应该打开所有室内积聚的公共场所的窗户进行开窗通风</strong>，这样就可以创造一个类似于室外空气流通的室内外通风的室内积聚环境，持续大风量用室外空气置换室内空气，防止室内空气中新冠病毒气溶胶的积累，就可能减少积聚性感染事件的发生。</p>\n<p data-pid=\"aj-yLbP0\">在室内公共积聚场所，除了打开所有窗户进行大风量室内外通风，并且戴好口罩，减少停留时间等必要的措施以外，在有固定座位并且没法戴口罩的室内场所，如餐饮、酒吧、茶馆、咖啡厅等室内场所，能够保障吸入前杀死空气中的新冠病毒，使其不具传染能力，就能大幅度减少积聚时感染新冠肺炎事件发生。</p>\n<p data-pid=\"JLVRCqVm\">在香港，<strong>室内外温差相差不大时，应该打开所有家中的窗户进行开窗通风</strong>，创造一个类似于室外空气流通的室内外通风居家环境，持续大风量用室外空气置换室内空气，防止室内空气中新冠病毒气溶胶的积累，就可能减少家庭感染事件的发生。</p>\n<p data-pid=\"8T_hevOc\"><strong>在任何时候做好个人防疫措施都十分重要，无论你所在的地区有疫情，还是没疫情。个人防疫措施无非就是接种疫苗、戴口罩、勤洗手、勤通风、不集聚、保持社交距离。</strong></p>\n<p data-pid=\"tElijM9U\"><strong>身体有了发热、干咳、乏力等与新冠肺炎症状类似不适，及早就医，既便于感染了新冠病毒早发现、早治疗；也避免把新冠病毒传染给家人、同事；更有利于及早查清传染链，为防疫做贡献。发热、干咳、乏力等症状是官方防疫公布的新冠病毒感染症状。</strong></p>\n</div>\n</div>', 1, 1, '2022-02-11 21:37:13', '2022-02-11 21:36:57', '2022-02-12 17:32:42', NULL);
INSERT INTO `zy_article` VALUES ('5d4968c2cb2b8c90c58fd95506cba45d', '59930dbd9a8201dc84aa5795b624af7f', 1, '1', 'CSS3', 'js数组常用的方法有哪些？', '双重循环 + slice，找出重复的元素。虽然只要求找出重复元素，但应该注意要顺便去重，否则外循环还是会对重复元素进行一遍检查；用 flag 记录重复次数，只在第一次重复的时候将元素放入新数组', 0, 71, 'http://localhost:5220/zy-server/public/images?id=164466183089025c842ff5fae3.jpg', '<h2>前言</h2>\n<p data-pid=\"7u3aXfsA\">数组是 JavaScript 中常见数据类型之一，关于它的一些操作方法，我在这里做一下简单记录和总结。</p>\n<p data-pid=\"i12q6MhT\">今天主要介绍：</p>\n<ul>\n<li data-pid=\"WkfeATxN\">如何找出数组中的重复/非重复元素</li>\n<li data-pid=\"1GnHdzgs\">数组扁平化方法</li>\n</ul>\n<h2>找出数组中的重复元素或非重复元素</h2>\n<p data-pid=\"z0KodDWb\">&nbsp; &nbsp; &nbsp;双重循环 + slice，找出重复的元素。虽然只要求找出重复元素，但应该注意要顺便去重，否则外循环还是会对重复元素进行一遍检查；用 flag 记录重复次数，只在第一次重复的时候将元素放入新数组</p>\n<pre class=\"language-javascript\"><code>function search(arr){\n    let res = []\n    let flag = 0\n    for(let i = 0;i &lt; arr.length;i++){\n        for(let j = i+1;j&lt;arr.length;j++){\n        	if(arr[i] === arr[j]){\n                flag++\n                if(flag == 1) res.push(arr[i])\n                arr.splice(j,1)\n            }    \n        }\n        flag = 0\n    }\n    return res\n}\n</code></pre>\n<div><br />\n<div>\n<p data-pid=\"zTGZSFc_\">map + filter，记录每个元素出现的次数。有了重复次数，就可以筛选出重复元素、重复次数最多元素或者非重复元素：</p>\n<div class=\"highlight\">\n<pre class=\"language-javascript\"><code>function search(arr){\n    const map = new Map()\n    for(item of arr){\n        if(!map.has(item)){\n            map.set(item,1)\n        } else {\n            map.set(item,map.get(item)+1)\n        }\n    }\n    // 找出重复元素，即出现次数大于1\n    return [...map.entries()].filter(item =&gt; item[1] &gt; 1).map(item =&gt; item[0])\n    // 找出非重复元素，即出现次数等于1\n    return [...map.entries()].filter(item =&gt; item[1] == 1).map(item =&gt; item[0])\n    // 找出重复次数最多的元素\n    return [...map.entries()]\n        .filter(item =&gt; item[1] == Math.max(...map.values()))\n    	.map(item =&gt; item[0])\n}</code></pre>\n</div>\n<h2>数组扁平化 / 数组降维</h2>\n<p data-pid=\"LtKZkJtF\"><a class=\"css-1occaib\" href=\"https://www.zhihu.com/search?q=%E4%BA%8C%E7%BB%B4%E6%95%B0%E7%BB%84&amp;search_source=Entity&amp;hybrid_search_source=Entity&amp;hybrid_search_extra=%7B%22sourceType%22%3A%22answer%22%2C%22sourceId%22%3A2337060474%7D\" target=\"_blank\" rel=\"noopener\" data-za-not-track-link=\"true\">二维数组</a>，以 <span style=\"background-color: #f0f0f0;\"><code>[[],[{a:1}],[],[3,4],5]</code></span> 为例，降维后得到 <span style=\"background-color: #ebe6e6;\"><code>[{a:1},3,4,5]</code></span></p>\n<h2>二维数组：双重循环</h2>\n<p data-pid=\"gx1M8DCN\">需要检查是否每个元素都是数组</p>\n<div class=\"highlight\">\n<pre class=\"language-javascript\"><code>function flatten(arr){\n    const res = []\n    for(let i = 0;i &lt; arr.length; i++){\n        if(Array.isArray(arr[i])){\n			for(let j = 0;j &lt; arr[i].length;j++){\n            	res.push(arr[i][j])\n        	}            \n        } else {\n            res.push(arr[i])\n        }      \n    }\n    return res\n}</code></pre>\n</div>\n</div>\n</div>', 1, 1, '2022-02-12 16:54:39', '2022-02-12 16:54:24', '2022-02-12 19:03:04', NULL);
INSERT INTO `zy_article` VALUES ('c49c909ab9692a2e04d4aaeb6b36d20c', '4474eb61ee85e9882d1287a9e1742767', 1, '2', 'Vue.js', '谷爱凌的商业价值到底有多大，是否已经超越巅峰期的刘翔？', '2021~2022赛季自由式滑雪，谷爱凌包揽了所有分站赛冠军，在37天内狂揽6金2银1铜共9枚奖牌。目前代言品牌已达23个，包括：京东、中国移动、雅诗兰黛、元气森林、科勒、维多利亚的秘密以及路易威登、蒂芙尼、IWC万国表等奢侈品牌。如果北京冬奥会谷爱凌顺利夺冠，她的商业价值还会翻倍。', 0, 4, 'http://localhost:5220/zy-server/public/images?id=16446552139286bc28df832637.jpg', '<p>2021~2022赛季自由式滑雪，谷爱凌包揽了所有分站赛冠军，在37天内狂揽6金2银1铜共9枚奖牌。目前代言品牌已达23个，包括：京东、中国移动、雅诗兰黛、元气森林、科勒、维多利亚的秘密以及路易威登、蒂芙尼、IWC万国表等奢侈品牌。如果北京冬奥会谷爱凌顺利夺冠，她的商业价值还会翻倍。</p>\n<p>对比巅峰期的刘翔，2008年奥运前，刘翔身负14个代言，当年收入跃至1.63亿元。当年运动员商业价值仅次于姚主席。</p>\n<p>美少女白皮外貌+北京腔的标签换取了空前的关注度，如果在漂亮国她只是一名滑雪运动员，入籍中国，给她带来了空前的商业名利。</p>\n<p>预祝谷爱凌能够在冬奥赛场顺利夺冠。</p>\n<p>谷爱凌商业价值到底达到哪个高度？是否已经是国内现役排名第一，最终能超越姚明、刘翔、李娜等顶级运动明星？</p>', 0, 1, '2022-02-11 21:33:09', '2022-02-11 21:22:38', '2022-02-12 16:40:15', NULL);
